func GetLogo() string {
	info, err := host.Info()
	if err != nil {
		panic(err)
	}
	if strings.Contains(info.OS, "darwin") {
		return darwin
	}
	if strings.Contains(info.OS, "windows") {
		return windows
	}
	if strings.Contains(info.OS, "linux") {
		if strings.Contains(info.Platform, "ubuntu") {
			return ubuntu
		}
		return linux
	}

	return default_logo
}
